import { useState, useEffect, useCallback } from 'react';
import './App.css';
import SearchBar from './components/SearchBar';
import WeatherData from './components/WeatherData';
import { getWeatherData } from './services/WeatherService';

// const weatherData = [
//   {
//     day: "Monday",
//     temperature: 20,
//     condition: "Sunny"
//   },
//   {
//     day: "Tuesday",
//     temperature: 22,
//     condition: "Cloudy"
//   },
//   {
//     day: "Wednesday",
//     temperature: 18,
//     condition: "Rainy"
//   },
//   // Add more data as needed
// ];

function App() {
  const persistedLocation = localStorage.getItem('searchTerm');
  const [searchTerm, setSearchTerm] = useState(persistedLocation || 'New York, NY');
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const fetchDataCallback = useCallback(async () => {}, []);
  function handleChange(event) {
    setSearchTerm(event.target.value);
  }
  function handleSubmit(event) {
    event.preventDefault();
    console.log('Submitting');
  }
  useEffect(() => {
    localStorage.setItem('searchTerm', searchTerm);
  }, [searchTerm]);
  // use effect called initially, then dependency says run it once and if something changes, run again? no dependencies, don't run
  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        // throw new Error('error');
        const response = await getWeatherData();
        console.log(response.data.list);
        setWeatherData(response.data.list);
        setLoading(false);
      } catch {
        setError(true);
        setLoading(false);
      }
    }
    fetchData();
  }, []);
  console.log('non-memoized')
  return (
    <div id='main' className='container'>
      <SearchBar handleSubmit={handleSubmit} searchTerm={searchTerm} handleChange={handleChange} id={'search-city'}>
      <strong>Search City: {searchTerm} </strong>
      </SearchBar>
      {error && <p>There was an error coding your data</p>}
      {loading ? <p>Data Loading</p> :(
      <WeatherData list={weatherData} />
      )}
    </div>
  );
}

export default App;
